CREATE DATABASE IF NOT EXISTS booqueenDB;
USE booqueenDB;

CREATE TABLE IF NOT EXISTS users (
    id INT AUTO_INCREMENT PRIMARY KEY,
    username VARCHAR(255) NOT NULL UNIQUE,
    password VARCHAR(255) NOT NULL,
    firstName VARCHAR(255) NOT NULL,
    lastName VARCHAR(255) NOT NULL,
    address VARCHAR(255) NOT NULL,
    phoneNumber VARCHAR(20) NOT NULL,
    postalCode VARCHAR(10) NOT NULL,
    role VARCHAR(255)
);

CREATE TABLE IF NOT EXISTS catalogueDirector (
    name VARCHAR(40) PRIMARY KEY,
    fileNameImages VARCHAR(255) NOT NULL
);

CREATE TABLE IF NOT EXISTS catalogue (
    prix FLOAT,
    pays VARCHAR(255),
    ville VARCHAR(255) PRIMARY KEY,
    accroche VARCHAR(255),
    racineImg1 VARCHAR(255),
    racineImg2 VARCHAR(255),
    textVille1 VARCHAR(350),
    textVille2 VARCHAR(350),
    textVille3 VARCHAR(400),
    director_name VARCHAR(255),
    FOREIGN KEY (director_name) REFERENCES catalogueDirector(name)
);

CREATE TABLE IF NOT EXISTS reservations (
    username VARCHAR(70),
    ville VARCHAR(70),
    pays VARCHAR(70),
    prix FLOAT,
    FOREIGN KEY (username) REFERENCES users(username),
    FOREIGN KEY (ville) REFERENCES catalogue(ville)
);




